mapper.update:
update TR_ORDER
	set DELIVERY=#{delivery}
where oId=#{oId}

mapper.select:
Select o.oId,m.userName,m.userPhone,o.rName,o.rPhone,o.rAddr,p.pName,d.count,o.oDate,o.delivery
from tr_order o,tr_member m,tr_product p,tr_orderDetails d
where o.oId=#{oId}
and m.userId=o.userId
and d.oId=#{oId}
and d.pNo=p.pNo

mapper.update:
UPDATE tr_order
SET oId=#{oId},
	rName=#{rName},
	rPhone=#{rPhone},
	rAddr=#{rAddr}
where oId=#{oId}

mapper.select:
SELECT qNo,qTitle,qWriter,qContent,qDate
FROM tr_qna
where qNo=#{qNo}

SELECT qNo,rNo,rContent,rWriter,rDate
FROM tr_reply
where qNo=#{qNo}

select qNo,rNo,rContent,rWriter,rDate
From tr_reply
where rNo=#{rNo}
